// SPDX-License-Identifier: MIT

pragma solidity ^0.8.11;

contract MicgroGridContract {


    struct EnergyOffer {
        address asset;
        uint256 price;
        uint startDate;
        uint endDate;
    }



    mapping (address => EnergyOffer) public eWOffersList;



    function createEnergyOffer(address asset, uint256 price , uint startDate, uint endDate) external {  
        eWOffersList[msg.sender] = EnergyOffer(asset,price,startDate,endDate);
    }

    function returnMappingValue() external view returns (EnergyOffer memory) {
        return eWOffersList[msg.sender];
    }  
}
